     */
    function withdrawFees() external onlyOwner {
        uint256 fees = totalFeesCollected;
        require(fees > 0, "No fees to withdraw");
        
        totalFeesCollected = 0;
        IERC20(USDC).safeTransfer(owner(), fees);
        
        emit FeeWithdrawn(owner(), fees);
    }
    
    /**
